// src/App.jsx
import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Header from './components/Header/Header';
import Hero from './components/Hero/Hero';
import About from './components/About/About';
import Speakers from './components/Speakers/Speakers';
import Schedule from './components/Schedule/Schedule';
import Registration from './components/Registration/Registration';
import Contact from './components/Contact/Contact';
import Footer from './components/Footer/Footer';
import LoadingScreen from './components/LoadingScreen/LoadingScreen';
import ParticleBackground from './components/ParticleBackground/ParticleBackground';
import './index.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="min-h-screen bg-dark-400 text-white font-sans overflow-x-hidden">
      <ParticleBackground />

      {/* Background Elements */}
      <div className="fixed inset-0 bg-grid-pattern opacity-[0.02] pointer-events-none -z-10"></div>
      <div className="fixed inset-0 bg-gradient-to-br from-dark-400/50 via-dark-300/30 to-dark-500/50 pointer-events-none -z-20"></div>

      <AnimatePresence mode="wait">
        {isLoading ? (
          <LoadingScreen />
        ) : (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8 }}
            className="w-full"
          >
            <Header />
            <main className="w-full">
              <Hero />
              <About />
              <Speakers />
              <Schedule />
              <Registration />
              <Contact />
            </main>
            <Footer />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;

// src/components/About/About.jsx
import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';
import { Target, Users, Lightbulb, Globe, Rocket } from 'lucide-react';

const About = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, threshold: 0.2 });

  const features = [
    {
      icon: Target,
      title: "Our Mission",
      description: "To foster sustainable innovation through cutting-edge engineering research and global collaboration for a better tomorrow.",
      color: "from-accent-cyan to-primary-500"
    },
    {
      icon: Lightbulb,
      title: "Innovation Hub",
      description: "Showcasing groundbreaking research and innovative solutions that push the boundaries of sustainable technology.",
      color: "from-accent-amber to-accent-pink"
    },
    {
      icon: Users,
      title: "Global Community",
      description: "Connecting researchers, industry leaders, and students from 15+ countries to share knowledge and expertise.",
      color: "from-accent-purple to-primary-500"
    },
    {
      icon: Globe,
      title: "Sustainable Impact",
      description: "Addressing global challenges through engineering excellence and environmentally conscious practices.",
      color: "from-primary-500 to-accent-teal"
    }
  ];

  return (
    <section id="about" ref={ref} className="section-padding bg-dark-400/50">
      <div className="container-responsive">
        {/* Section Header */}
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.6 }}
        >
          <h2 className="text-3xl sm:text-4xl md:text-5xl font-bold mb-6">
            About <span className="text-gradient">ICSIE 2024</span>
          </h2>
          <p className="text-lg text-gray-300 max-w-3xl mx-auto leading-relaxed">
            The International Conference on Sustainable Innovation and Engineering brings together 
            the brightest minds to discuss and shape the future of sustainable technology and engineering practices.
          </p>
        </motion.div>

        {/* Features Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-16">
          {features.map((feature, index) => (
            <motion.div
              key={feature.title}
              className="glass-morphism rounded-2xl p-6 text-center"
              initial={{ opacity: 0, y: 30 }}
              animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              whileHover={{ y: -5, scale: 1.02 }}
            >
              <div className={`inline-flex p-3 rounded-xl bg-gradient-to-r ${feature.color} mb-4`}>
                <feature.icon className="w-6 h-6 text-white" />
              </div>
              <h3 className="text-xl font-semibold text-white mb-3">{feature.title}</h3>
              <p className="text-gray-400 text-sm leading-relaxed">{feature.description}</p>
            </motion.div>
          ))}
        </div>

        {/* Call to Action */}
        <motion.div
          className="glass-morphism-intense rounded-3xl p-8 text-center"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          <Rocket className="w-12 h-12 text-accent-amber mx-auto mb-4" />
          <h3 className="text-2xl font-bold text-white mb-4">Ready to Join the Innovation?</h3>
          <p className="text-gray-300 mb-6 max-w-2xl mx-auto">
            Be part of the most exciting sustainable engineering conference of the year. 
            Network with experts, learn from pioneers, and shape the future with us.
          </p>
          <motion.button
            className="bg-gradient-to-r from-primary-500 to-accent-purple text-white px-8 py-3 rounded-full font-semibold"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Register Now - Limited Seats!
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default About;

// src/components/Contact/Contact.jsx
import React, { useState } from "react";
import { motion } from "framer-motion";
import { useInView } from "framer-motion";
import { useRef } from "react";
import {
  Mail,
  Phone,
  MapPin,
  Send,
  Clock,
  Globe,
  MessageCircle,
} from "lucide-react";

const Contact = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, threshold: 0.2 });
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });

  const contactInfo = [
    {
      icon: Mail,
      title: "Email Us",
      info: "contact@icsie2024.org",
      description: "Send us an email anytime",
      color: "accent-cyan",
    },
    {
      icon: Phone,
      title: "Call Us",
      info: "+1 (555) 123-ICSIE",
      description: "Mon to Fri 9am to 6pm",
      color: "accent-purple",
    },
    {
      icon: MapPin,
      title: "Visit Us",
      info: "Nithish Hariharan College",
      description: "Conference Venue",
      color: "accent-amber",
    },
    {
      icon: Globe,
      title: "Website",
      info: "www.icsie2024.org",
      description: "Learn more online",
      color: "accent-pink",
    },
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log("Form submitted:", formData);
    alert("Thank you for your message! We will get back to you soon.");
    setFormData({ name: "", email: "", subject: "", message: "" });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut",
      },
    },
  };

  // Map semantic color keys to static Tailwind class names
  const bgClass = (key) =>
    ({
      "accent-cyan": "bg-accent-cyan",
      "accent-purple": "bg-accent-purple",
      "accent-amber": "bg-accent-amber",
      "accent-pink": "bg-accent-pink",
      "accent-teal": "bg-accent-teal",
      "accent-violet": "bg-accent-violet",
    }[key] || "bg-accent-cyan");

  return (
    <section id="contact" ref={ref} className="py-20 relative overflow-hidden">
      {/* Background Elements */}
      <div className="absolute top-0 left-0 w-96 h-96 bg-accent-cyan/10 rounded-full blur-3xl -translate-x-1/2 -translate-y-1/2"></div>
      <div className="absolute bottom-0 right-0 w-80 h-80 bg-accent-purple/10 rounded-full blur-3xl translate-x-1/2 translate-y-1/2"></div>

      <div className="container mx-auto px-6 relative z-10">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
        >
          {/* Section Header */}
          <motion.div className="text-center mb-16" variants={itemVariants}>
            <motion.div
              className="inline-flex items-center space-x-2 text-accent-amber mb-4"
              whileHover={{ scale: 1.05 }}
            >
              <MessageCircle className="w-5 h-5" />
              <span className="font-semibold">Get In Touch</span>
            </motion.div>
            <h2 className="text-5xl md:text-6xl font-display font-bold mb-6">
              Contact <span className="text-gradient">Us</span>
            </h2>
            <p className="text-xl text-gray-300 max-w-3xl mx-auto leading-relaxed">
              Have questions about the conference? We're here to help! Reach out
              to our team and we'll get back to you as soon as possible.
            </p>
          </motion.div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
            {/* Contact Information */}
            <motion.div variants={itemVariants}>
              <h3 className="text-3xl font-bold text-white mb-8">Let's Talk</h3>

              <div className="space-y-6 mb-8">
                {contactInfo.map((item, index) => (
                  <motion.div
                    key={index}
                    className="flex items-center space-x-4 p-6 glass-morphism rounded-2xl border border-white/10 hover:border-primary-500/30 transition-all duration-500 cursor-pointer"
                    whileHover={{ x: 10, scale: 1.02 }}
                  >
                    <div
                      className={`p-3 rounded-xl ${bgClass(
                        item.color
                      )} flex-shrink-0`}
                    >
                      <item.icon className="w-6 h-6 text-white" />
                    </div>
                    <div>
                      <h4 className="text-lg font-semibold text-white mb-1">
                        {item.title}
                      </h4>
                      <p className="text-accent-cyan font-medium">
                        {item.info}
                      </p>
                      <p className="text-gray-400 text-sm">
                        {item.description}
                      </p>
                    </div>
                  </motion.div>
                ))}
              </div>

              {/* Additional Info */}
              <motion.div
                className="glass-morphism-intense rounded-2xl p-6"
                whileHover={{ y: -5 }}
              >
                <div className="flex items-center space-x-3 mb-4">
                  <Clock className="w-5 h-5 text-accent-amber" />
                  <h4 className="text-lg font-semibold text-white">
                    Response Time
                  </h4>
                </div>
                <p className="text-gray-300">
                  We typically respond to all inquiries within{" "}
                  <span className="text-accent-cyan font-semibold">
                    24 hours
                  </span>
                  . For urgent matters, please call us directly.
                </p>
              </motion.div>
            </motion.div>

            {/* Contact Form */}
            <motion.div variants={itemVariants}>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-white font-semibold mb-2">
                      Full Name
                    </label>
                    <motion.input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      className="w-full px-4 py-3 glass-morphism rounded-xl border border-white/10 focus:border-primary-500/50 focus:outline-none transition-colors duration-300 text-white placeholder-gray-400"
                      placeholder="Enter your name"
                      whileFocus={{ scale: 1.02 }}
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-white font-semibold mb-2">
                      Email Address
                    </label>
                    <motion.input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      className="w-full px-4 py-3 glass-morphism rounded-xl border border-white/10 focus:border-primary-500/50 focus:outline-none transition-colors duration-300 text-white placeholder-gray-400"
                      placeholder="Enter your email"
                      whileFocus={{ scale: 1.02 }}
                      required
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-white font-semibold mb-2">
                    Subject
                  </label>
                  <motion.input
                    type="text"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    className="w-full px-4 py-3 glass-morphism rounded-xl border border-white/10 focus:border-primary-500/50 focus:outline-none transition-colors duration-300 text-white placeholder-gray-400"
                    placeholder="What's this about?"
                    whileFocus={{ scale: 1.02 }}
                    required
                  />
                </div>

                <div>
                  <label className="block text-white font-semibold mb-2">
                    Message
                  </label>
                  <motion.textarea
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    rows="6"
                    className="w-full px-4 py-3 glass-morphism rounded-xl border border-white/10 focus:border-primary-500/50 focus:outline-none transition-colors duration-300 resize-none text-white placeholder-gray-400"
                    placeholder="Tell us about your inquiry..."
                    whileFocus={{ scale: 1.02 }}
                    required
                  />
                </div>

                <motion.button
                  type="submit"
                  className="w-full bg-gradient-to-r from-primary-500 to-accent-purple text-white py-4 rounded-2xl font-bold text-lg shadow-lg hover:shadow-xl transition-all duration-500 flex items-center justify-center space-x-3"
                  whileHover={{ scale: 1.02, y: -2 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <Send className="w-5 h-5" />
                  <span>Send Message</span>
                </motion.button>
              </form>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Contact;

// src/components/Footer/Footer.jsx
import React from 'react';
import { motion } from 'framer-motion';
import { Heart, ArrowUp, Mail, Phone, MapPin } from 'lucide-react';

const Footer = () => {
  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const quickLinks = [
    { name: 'Home', href: '#home' },
    { name: 'About', href: '#about' },
    { name: 'Speakers', href: '#speakers' },
    { name: 'Schedule', href: '#schedule' },
    { name: 'Register', href: '#register' },
    { name: 'Contact', href: '#contact' }
  ];

  return (
    <footer className="bg-dark-500 border-t border-white/10">
      <div className="container-responsive py-12">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {/* Brand Section */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <div className="flex items-center space-x-3 mb-4">
              <div className="p-2 bg-gradient-to-r from-primary-500 to-accent-purple rounded-lg">
                <Heart className="w-6 h-6 text-white" />
              </div>
              <span className="text-xl font-bold text-white">ICSIE 2024</span>
            </div>
            <p className="text-gray-400 text-sm mb-4">
              International Conference on Sustainable Innovation and Engineering. 
              Shaping the future through technology and sustainability.
            </p>
          </motion.div>

          {/* Quick Links */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.1 }}
          >
            <h3 className="text-lg font-semibold text-white mb-4">Quick Links</h3>
            <ul className="space-y-2">
              {quickLinks.map((link, index) => (
                <li key={link.name}>
                  <a
                    href={link.href}
                    className="text-gray-400 hover:text-accent-cyan transition-colors duration-300 text-sm"
                  >
                    {link.name}
                  </a>
                </li>
              ))}
            </ul>
          </motion.div>

          {/* Contact Info */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <h3 className="text-lg font-semibold text-white mb-4">Contact Info</h3>
            <div className="space-y-3">
              <div className="flex items-center space-x-3">
                <MapPin className="w-4 h-4 text-accent-cyan" />
                <span className="text-gray-400 text-sm">Nithish Hariharan College</span>
              </div>
              <div className="flex items-center space-x-3">
                <Mail className="w-4 h-4 text-accent-cyan" />
                <span className="text-gray-400 text-sm">contact@icsie2024.org</span>
              </div>
              <div className="flex items-center space-x-3">
                <Phone className="w-4 h-4 text-accent-cyan" />
                <span className="text-gray-400 text-sm">+1 (555) 123-ICSIE</span>
              </div>
            </div>
          </motion.div>

          {/* Newsletter */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.3 }}
          >
            <h3 className="text-lg font-semibold text-white mb-4">Stay Updated</h3>
            <p className="text-gray-400 text-sm mb-4">
              Get the latest news and updates about ICSIE 2024.
            </p>
            <div className="flex space-x-2">
              <input
                type="email"
                placeholder="Enter your email"
                className="flex-1 px-3 py-2 glass-morphism rounded-lg border border-white/10 focus:border-primary-500/50 focus:outline-none text-sm"
              />
              <motion.button
                className="px-4 py-2 bg-gradient-to-r from-primary-500 to-accent-purple text-white rounded-lg font-semibold text-sm"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Subscribe
              </motion.button>
            </div>
          </motion.div>
        </div>

        {/* Bottom Bar */}
        <div className="border-t border-white/10 mt-8 pt-8 flex flex-col md:flex-row justify-between items-center space-y-4 md:space-y-0">
          <p className="text-gray-400 text-sm flex items-center space-x-2">
            <span>Made with</span>
            <Heart className="w-4 h-4 text-accent-pink" fill="currentColor" />
            <span>by ICSIE Team â€¢ Â© 2024 All rights reserved</span>
          </p>

          <motion.button
            onClick={scrollToTop}
            className="p-2 glass-morphism rounded-lg hover:bg-primary-500/20 transition-colors duration-300"
            whileHover={{ scale: 1.1, y: -2 }}
            whileTap={{ scale: 0.9 }}
          >
            <ArrowUp className="w-5 h-5 text-gray-400" />
          </motion.button>
        </div>
      </div>
    </footer>
  );
};

export default Footer;

// src/components/Header/Header.jsx
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Menu, X, Calendar } from 'lucide-react';

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const menuItems = [
    { name: 'Home', href: '#home' },
    { name: 'About', href: '#about' },
    { name: 'Speakers', href: '#speakers' },
    { name: 'Schedule', href: '#schedule' },
    { name: 'Register', href: '#register' },
    { name: 'Contact', href: '#contact' },
  ];

  return (
    <header className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
      isScrolled ? 'bg-dark-400/95 backdrop-blur-md py-3 shadow-lg' : 'bg-transparent py-4'
    }`}>
      <nav className="container">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-gradient-to-r from-primary-500 to-accent-purple rounded-lg">
              <Calendar className="w-6 h-6 text-white" />
            </div>
            <div>
              <div className="text-xl font-bold text-white">ICSIE 2024</div>
              <div className="text-xs text-gray-400 hidden sm:block">International Conference</div>
            </div>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden lg:flex items-center space-x-8">
            {menuItems.map((item) => (
              <a
                key={item.name}
                href={item.href}
                className="text-gray-300 hover:text-white transition-colors duration-300 font-medium text-sm uppercase tracking-wide"
              >
                {item.name}
              </a>
            ))}
          </div>

          {/* Mobile Menu Button */}
          <button
            className="lg:hidden p-2 rounded-lg bg-dark-300 hover:bg-dark-200 transition-colors"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          >
            {isMobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
          </button>
        </div>

        {/* Mobile Menu */}
        <AnimatePresence>
          {isMobileMenuOpen && (
            <motion.div
              className="lg:hidden absolute top-full left-0 right-0 bg-dark-400/95 backdrop-blur-lg border-t border-gray-700 mt-2"
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
            >
              <div className="py-4 space-y-1">
                {menuItems.map((item) => (
                  <a
                    key={item.name}
                    href={item.href}
                    className="block px-6 py-3 text-gray-300 hover:text-white hover:bg-dark-300 transition-all duration-300 text-lg font-medium"
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    {item.name}
                  </a>
                ))}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </nav>
    </header>
  );
};

export default Header;

// src/components/Hero/Hero.jsx
import React from 'react';
import { motion } from 'framer-motion';
import { Calendar, MapPin, Clock, ArrowRight } from 'lucide-react';

const Hero = () => {
  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative pt-20">
      <div className="container-responsive text-center">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          {/* Conference Badge */}
          <motion.div
            className="inline-flex items-center space-x-2 bg-dark-300/50 backdrop-blur-sm border border-primary-500/30 rounded-full px-6 py-3 mb-8"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ delay: 0.2 }}
          >
            <div className="w-2 h-2 bg-accent-cyan rounded-full animate-pulse"></div>
            <span className="text-sm text-gray-300">International Conference on Sustainable Innovation and Engineering</span>
          </motion.div>

          {/* Main Title */}
          <motion.h1
            className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold mb-6"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <span className="text-gradient">ICSIE 2024</span>
          </motion.h1>

          <motion.p
