              whileTap={{ scale: 0.95 }}
            >
              View Schedule
            </motion.button>
          </motion.div>

          {/* Stats */}
          <motion.div
            className="grid grid-cols-2 sm:grid-cols-4 gap-8 max-w-4xl mx-auto"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.7 }}
          >
            {[
              { number: '50+', label: 'Expert Speakers' },
              { number: '1000+', label: 'Participants' },
              { number: '30+', label: 'Tech Sessions' },
              { number: '15+', label: 'Countries' },
            ].map((stat, index) => (
              <motion.div
                key={stat.label}
                className="text-center"
                whileHover={{ y: -5 }}
                transition={{ type: "spring", stiffness: 300 }}
              >
                <div className="text-2xl sm:text-3xl font-bold text-gradient mb-2">
                  {stat.number}
                </div>
                <div className="text-sm text-gray-400">{stat.label}</div>
              </motion.div>
            ))}
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;

// src/components/LoadingScreen/LoadingScreen.jsx
import React from 'react';
import { motion } from 'framer-motion';
import { Sparkles, Zap } from 'lucide-react';

const LoadingScreen = () => {
  return (
    <motion.div
      className="fixed inset-0 bg-dark-400 flex items-center justify-center z-50"
      initial={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="text-center">
        <motion.div
          className="inline-flex items-center space-x-4 mb-8"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <motion.div
            className="p-4 bg-gradient-to-br from-primary-500 to-accent-purple rounded-2xl shadow-2xl"
            animate={{ rotate: 360 }}
            transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
          >
            <Zap className="w-8 h-8 text-white" />
          </motion.div>
          <div>
            <h1 className="text-4xl font-black text-gradient">ICSIE 2024</h1>
            <p className="text-gray-400 text-sm mt-1">Loading Innovation...</p>
          </div>
        </motion.div>

        <div className="flex justify-center space-x-2">
          {[0, 1, 2].map((i) => (
            <motion.div
              key={i}
              className="w-3 h-3 bg-gradient-to-r from-primary-400 to-accent-purple rounded-full"
              animate={{ y: [0, -10, 0] }}
              transition={{
                duration: 1,
                repeat: Infinity,
                delay: i * 0.2,
              }}
            />
          ))}
        </div>

        <motion.div
          className="mt-8 text-gray-400 text-sm"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
        >
          <Sparkles className="w-4 h-4 inline mr-2" />
          Preparing an extraordinary experience...
        </motion.div>
      </div>
    </motion.div>
  );
};

export default LoadingScreen;

// src/components/Registration/Registration.jsx
import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';
import { Check, Star, Shield } from 'lucide-react';

const Registration = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, threshold: 0.2 });
  const [selectedPlan, setSelectedPlan] = useState('standard');

  const pricingPlans = [
    {
      id: 'student',
      name: 'Student Pass',
      price: '$49',
      originalPrice: '$99',
      description: 'Perfect for students and young researchers',
      popular: false,
      features: [
        'Access to all conference sessions',
        'Student networking events',
        'Lunch and refreshments',
        'Digital certificate',
        'Conference materials'
      ]
    },
    {
      id: 'standard',
      name: 'Standard Pass',
      price: '$199',
      originalPrice: '$299',
      description: 'Ideal for professionals and academics',
      popular: true,
      features: [
        'All Student Pass benefits',
        'Access to workshops',
        'Conference dinner',
        'Physical certificate',
        'Priority seating',
        'Networking app access'
      ]
    },
    {
      id: 'vip',
      name: 'VIP Experience',
      price: '$499',
      originalPrice: '$699',
      description: 'Ultimate conference experience',
      popular: false,
      features: [
        'All Standard Pass benefits',
        'VIP lounge access',
        'Meet & greet with speakers',
        'Premium seating',
        'Gift package',
        'One-year membership'
      ]
    }
  ];

  return (
    <section id="register" ref={ref} className="section-padding">
      <div className="container-responsive">
        {/* Section Header */}
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.6 }}
        >
          <h2 className="text-3xl sm:text-4xl md:text-5xl font-bold mb-6">
            Registration <span className="text-gradient">Plans</span>
          </h2>
          <p className="text-lg text-gray-300 max-w-2xl mx-auto">
            Choose the perfect plan for your conference experience. Early bird discounts available for limited time only!
          </p>
        </motion.div>

        {/* Pricing Plans */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {pricingPlans.map((plan, index) => (
            <motion.div
              key={plan.id}
              className={`relative glass-morphism rounded-2xl p-6 ${
                selectedPlan === plan.id ? 'border-2 border-primary-500' : 'border border-white/10'
              }`}
              initial={{ opacity: 0, y: 30 }}
              animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              whileHover={{ y: -5 }}
              onClick={() => setSelectedPlan(plan.id)}
            >
              {/* Popular Badge */}
              {plan.popular && (
                <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                  <div className="bg-gradient-to-r from-accent-amber to-accent-pink text-white px-4 py-1 rounded-full text-sm font-bold flex items-center space-x-1">
                    <Star className="w-3 h-3" fill="currentColor" />
                    <span>MOST POPULAR</span>
                  </div>
                </div>
              )}

              {/* Plan Header */}
              <div className="text-center mb-6">
                <h3 className="text-xl font-bold text-white mb-2">{plan.name}</h3>
                <p className="text-gray-400 text-sm mb-4">{plan.description}</p>
                
                <div className="flex items-baseline justify-center space-x-2 mb-2">
                  <span className="text-3xl font-black text-white">{plan.price}</span>
                  <span className="text-lg text-gray-400 line-through">{plan.originalPrice}</span>
                </div>
                <div className="text-accent-cyan text-sm font-semibold">Save 50% - Early Bird</div>
              </div>

              {/* Features */}
              <div className="space-y-3 mb-6">
                {plan.features.map((feature, featureIndex) => (
                  <div key={featureIndex} className="flex items-center space-x-3">
                    <div className="w-5 h-5 bg-green-500/20 rounded-full flex items-center justify-center flex-shrink-0">
                      <Check className="w-3 h-3 text-green-400" />
                    </div>
                    <span className="text-gray-300 text-sm">{feature}</span>
                  </div>
                ))}
              </div>

              {/* CTA Button */}
              <motion.button
                className={`w-full py-3 rounded-xl font-semibold ${
                  selectedPlan === plan.id
                    ? 'bg-gradient-to-r from-primary-500 to-accent-purple text-white'
                    : 'glass-morphism text-primary-400'
                }`}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                {selectedPlan === plan.id ? 'Selected Plan' : 'Choose Plan'}
              </motion.button>

              {/* Security Badge */}
              <div className="flex items-center justify-center space-x-2 mt-4 text-gray-400 text-sm">
                <Shield className="w-4 h-4" />
                <span>Secure Payment</span>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Final CTA */}
        <motion.div
          className="text-center mt-16"
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          <motion.button
            className="bg-gradient-to-r from-accent-amber to-accent-pink text-white px-8 py-4 rounded-full font-bold text-lg"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Register Now - Limited Seats Available!
          </motion.button>
          <p className="text-gray-400 mt-4">
            ðŸ”¥ <span className="text-accent-amber font-semibold">50% discount</span> ends soon!
          </p>
        </motion.div>
      </div>
    </section>
  );
};

export default Registration;

// src/components/Schedule/Schedule.jsx
import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';
import { Clock, MapPin, Users, Calendar, ChevronDown, Play, Pause, BookOpen } from 'lucide-react';

const Schedule = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, threshold: 0.2 });
  const [activeDay, setActiveDay] = useState(1);
  const [expandedSession, setExpandedSession] = useState(null);

  const scheduleData = {
    1: {
      date: "December 15, 2024",
      title: "Day 1: Innovation & Technology",
      theme: "Exploring cutting-edge technologies and innovative solutions",
      sessions: [
        {
          id: 1,
          time: "09:00 - 10:00",
          title: "Opening Ceremony & Keynote",
          speaker: "Dr. Sarah Chen",
          location: "Main Auditorium",
          type: "keynote",
          description: "Welcome address and opening keynote on the future of sustainable innovation",
          capacity: "500 attendees",
          tags: ["Opening", "Keynote", "All Attendees"]
        },
        {
          id: 2,
          time: "10:30 - 12:00",
          title: "AI for Sustainable Development",
          speaker: "Dr. Emily Watson & Team",
          location: "Tech Hall A",
          type: "workshop",
          description: "Hands-on workshop exploring AI applications in sustainable development and environmental protection",
          capacity: "100 attendees",
          tags: ["AI", "Workshop", "Hands-on"]
        },
        {
          id: 3,
          time: "13:30 - 15:00",
          title: "Green Energy Solutions Panel",
          speaker: "Prof. Michael Rodriguez et al.",
          location: "Main Auditorium",
          type: "panel",
          description: "Expert panel discussion on the latest advancements in renewable energy and sustainable power solutions",
          capacity: "300 attendees",
          tags: ["Energy", "Panel", "Discussion"]
        },
        {
          id: 4,
          time: "15:30 - 17:00",
          title: "Robotics in Sustainable Manufacturing",
          speaker: "James Kumar",
          location: "Innovation Lab",
          type: "demo",
          description: "Live demonstration of robotic systems designed for eco-friendly manufacturing processes",
          capacity: "80 attendees",
          tags: ["Robotics", "Demo", "Manufacturing"]
        }
      ]
    },
    2: {
      date: "December 16, 2024",
      title: "Day 2: Implementation & Impact",
      theme: "Turning ideas into real-world sustainable solutions",
      sessions: [
        {
          id: 5,
          time: "09:30 - 11:00",
          title: "Smart Cities of the Future",
          speaker: "Alex Thompson",
          location: "Urban Tech Hall",
          type: "keynote",
          description: "Visionary talk on integrating technology and sustainability in urban development",
          capacity: "400 attendees",
          tags: ["Smart Cities", "Keynote", "Urban Planning"]
        },
        {
          id: 6,
          time: "11:30 - 13:00",
          title: "Biotech for Environmental Solutions",
          speaker: "Dr. Lisa Wang",
          location: "Bio Lab",
          type: "workshop",
          description: "Interactive session on biotechnology applications for environmental cleanup and sustainability",
          capacity: "60 attendees",
          tags: ["Biotech", "Workshop", "Environment"]
        },
        {
          id: 7,
          time: "14:30 - 16:00",
          title: "Startup Innovation Showcase",
          speaker: "Various Innovators",
          location: "Startup Pavilion",
          type: "showcase",
          description: "Showcase of groundbreaking startups working on sustainable engineering solutions",
          capacity: "200 attendees",
          tags: ["Startups", "Showcase", "Innovation"]
        },
        {
          id: 8,
          time: "16:30 - 18:00",
          title: "Closing Ceremony & Awards",
          speaker: "Conference Committee",
          location: "Main Auditorium",
          type: "ceremony",
          description: "Closing remarks, awards ceremony, and networking reception",
          capacity: "500 attendees",
          tags: ["Closing", "Awards", "Networking"]
        }
      ]
    }
  };

  const getTypeColor = (type) => {
    const colors = {
      keynote: "from-accent-amber to-accent-pink",
      workshop: "from-accent-cyan to-primary-500",
      panel: "from-accent-purple to-primary-500",
      demo: "from-primary-500 to-accent-teal",
      showcase: "from-accent-purple to-accent-pink",
      ceremony: "from-accent-amber to-accent-cyan"
    };
    return colors[type] || "from-primary-500 to-accent-purple";
  };

  const getTypeIcon = (type) => {
    const icons = {
      keynote: Play,
      workshop: BookOpen,
      panel: Users,
      demo: Play,
      showcase: Users,
      ceremony: Calendar
    };
    return icons[type] || Clock;
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <section id="schedule" ref={ref} className="py-20 relative overflow-hidden">
      {/* Background Elements */}
      <div className="absolute top-0 left-1/2 transform -translate-x-1/2 w-96 h-96 bg-accent-cyan/10 rounded-full blur-3xl -translate-y-1/2"></div>
      <div className="absolute bottom-0 right-1/4 w-80 h-80 bg-accent-purple/10 rounded-full blur-3xl translate-y-1/2"></div>

      <div className="container mx-auto px-6 relative z-10">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
        >
          {/* Section Header */}
          <motion.div className="text-center mb-16" variants={itemVariants}>
            <motion.div
              className="inline-flex items-center space-x-2 text-accent-amber mb-4"
              whileHover={{ scale: 1.05 }}
            >
              <Calendar className="w-5 h-5" />
              <span className="font-semibold">Conference Schedule</span>
            </motion.div>
            <h2 className="text-5xl md:text-6xl font-display font-bold mb-6">
              Event <span className="text-gradient">Timeline</span>
            </h2>
            <p className="text-xl text-gray-300 max-w-3xl mx-auto leading-relaxed">
              Two days packed with insightful sessions, hands-on workshops, and networking opportunities 
              with the brightest minds in sustainable engineering.
            </p>
          </motion.div>

          {/* Day Selector */}
          <motion.div className="flex justify-center mb-12" variants={itemVariants}>
            <div className="glass-morphism rounded-2xl p-2 flex space-x-2">
              {[1, 2].map((day) => (
                <motion.button
                  key={day}
                  onClick={() => setActiveDay(day)}
                  className={`px-8 py-4 rounded-xl font-bold text-lg transition-all duration-300 ${
                    activeDay === day
                      ? 'bg-gradient-to-r from-primary-500 to-accent-purple text-white shadow-lg'
                      : 'text-gray-300 hover:text-white hover:bg-dark-300/50'
                  }`}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Day {day}
                </motion.button>
              ))}
            </div>
          </motion.div>

          {/* Day Info */}
          <motion.div 
            className="text-center mb-12"
            key={activeDay}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <h3 className="text-3xl font-bold text-white mb-2">{scheduleData[activeDay].title}</h3>
            <p className="text-xl text-accent-cyan mb-2">{scheduleData[activeDay].date}</p>
            <p className="text-gray-400">{scheduleData[activeDay].theme}</p>
          </motion.div>

          {/* Schedule Timeline */}
          <motion.div className="max-w-4xl mx-auto" variants={itemVariants}>
            <div className="relative">
              {/* Timeline Line */}
              <div className="absolute left-8 top-0 bottom-0 w-1 bg-gradient-to-b from-primary-500 to-accent-purple rounded-full"></div>
              
              <div className="space-y-8">
                {scheduleData[activeDay].sessions.map((session, index) => {
                  const IconComponent = getTypeIcon(session.type);
                  return (
                    <motion.div
                      key={session.id}
                      className="relative flex items-start space-x-6 group cursor-pointer"
                      initial={{ opacity: 0, x: -50 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                      onClick={() => setExpandedSession(expandedSession === session.id ? null : session.id)}
                    >
                      {/* Timeline Node */}
                      <div className="relative z-10 flex-shrink-0">
                        <motion.div
                          className={`w-16 h-16 rounded-2xl bg-gradient-to-r ${getTypeColor(session.type)} flex items-center justify-center shadow-lg`}
                          whileHover={{ scale: 1.1, rotate: 5 }}
                        >
                          <IconComponent className="w-6 h-6 text-white" />
                        </motion.div>
                        <div className="absolute -left-4 top-1/2 transform -translate-y-1/2 w-8 h-1 bg-gradient-to-r from-primary-500 to-accent-purple"></div>
                      </div>

                      {/* Session Card */}
                      <motion.div
                        className={`flex-1 glass-morphism rounded-2xl p-6 border transition-all duration-500 ${
                          expandedSession === session.id 
                            ? 'border-primary-500/50 bg-primary-500/10' 
                            : 'border-white/10 hover:border-primary-500/30'
                        }`}
                        whileHover={{ y: -5 }}
                        layout
                      >
                        <div className="flex items-start justify-between mb-4">
                          <div className="flex-1">
                            <div className="flex items-center space-x-4 mb-2">
                              <span className="text-accent-cyan font-bold text-lg">{session.time}</span>
                              <span className={`px-3 py-1 rounded-full text-xs font-bold bg-gradient-to-r ${getTypeColor(session.type)} text-white`}>
                                {session.type.toUpperCase()}
                              </span>
                            </div>
                            <h3 className="text-2xl font-bold text-white mb-2">{session.title}</h3>
                            <p className="text-accent-purple font-semibold">{session.speaker}</p>
                          </div>
                          <motion.div
                            animate={{ rotate: expandedSession === session.id ? 180 : 0 }}
                            transition={{ duration: 0.3 }}
                          >
                            <ChevronDown className="w-5 h-5 text-gray-400" />
                          </motion.div>
                        </div>

                        <div className="flex items-center space-x-6 text-sm text-gray-400 mb-4">
                          <div className="flex items-center space-x-2">
                            <MapPin className="w-4 h-4" />
                            <span>{session.location}</span>
                          </div>
                          <div className="flex items-center space-x-2">
                            <Users className="w-4 h-4" />
                            <span>{session.capacity}</span>
                          </div>
                        </div>

                        {/* Tags */}
                        <div className="flex flex-wrap gap-2 mb-4">
                          {session.tags.map((tag, tagIndex) => (
                            <span
                              key={tagIndex}
                              className="px-3 py-1 bg-dark-300 text-gray-400 rounded-full text-xs font-medium"
                            >
                              {tag}
                            </span>
                          ))}
                        </div>

                        {/* Expandable Content */}
                        <AnimatePresence>
                          {expandedSession === session.id && (
                            <motion.div
                              initial={{ opacity: 0, height: 0 }}
                              animate={{ opacity: 1, height: 'auto' }}
                              exit={{ opacity: 0, height: 0 }}
                              transition={{ duration: 0.3 }}
                              className="overflow-hidden"
                            >
                              <p className="text-gray-300 leading-relaxed mb-4">{session.description}</p>
                              <div className="flex space-x-4">
                                <motion.button
                                  className="px-6 py-2 bg-primary-500 text-white rounded-xl font-semibold hover:bg-primary-600 transition-colors"
                                  whileHover={{ scale: 1.05 }}
                                  whileTap={{ scale: 0.95 }}
                                >
                                  Add to Calendar
                                </motion.button>
                                <motion.button
                                  className="px-6 py-2 glass-morphism text-primary-400 rounded-xl font-semibold hover:border-primary-500/50 transition-colors"
                                  whileHover={{ scale: 1.05 }}
                                  whileTap={{ scale: 0.95 }}
                                >
                                  Save Session
                                </motion.button>
                              </div>
                            </motion.div>
                          )}
                        </AnimatePresence>
                      </motion.div>
                    </motion.div>
                  );
                })}
              </div>
            </div>
          </motion.div>

          {/* Download Schedule CTA */}
          <motion.div className="text-center mt-16" variants={itemVariants}>
            <motion.button
              className="bg-gradient-to-r from-primary-500 to-accent-purple text-white px-12 py-4 rounded-2xl font-bold text-lg shadow-2xl hover:shadow-3xl transition-all duration-500"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
            >
              Download Full Schedule (PDF)
            </motion.button>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Schedule;

// src/components/Speakers/Speakers.jsx
import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'framer-motion';
import { useRef } from 'react';
import { Mail, Globe, Award, Calendar, X, ExternalLink, Star } from 'lucide-react';

const Speakers = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, threshold: 0.2 });
  const [selectedSpeaker, setSelectedSpeaker] = useState(null);
  const [filter, setFilter] = useState('all');

  const speakers = [
    {
      id: 1,
      name: "Dr. Sarah Chen",
      title: "AI Research Director",
      company: "TechInnovate Inc.",
      image: "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=400&h=400&fit=crop&crop=face",
      category: "ai",
      featured: true,
      bio: "Leading researcher in sustainable AI applications with 15+ years of experience in machine learning and environmental science.",
      topics: ["Sustainable AI", "Machine Learning", "Climate Tech"],
      social: {
        twitter: "#",
        linkedin: "#",
        website: "#"
      }
    },
    {
      id: 2,
      name: "Prof. Michael Rodriguez",
      title: "Head of Engineering",
      company: "GreenTech University",
      image: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=400&h=400&fit=crop&crop=face",
      category: "sustainability",
      featured: true,
      bio: "Pioneer in renewable energy systems and sustainable infrastructure development. Author of 5 books on green engineering.",
      topics: ["Renewable Energy", "Green Infrastructure", "Circular Economy"],
      social: {
        twitter: "#",
        linkedin: "#",
        website: "#"
      }
    },
    {
      id: 3,
      name: "Dr. Emily Watson",
      title: "Chief Innovation Officer",
      company: "Future Systems Ltd.",
      image: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=400&h=400&fit=crop&crop=face",
      category: "innovation",
      featured: false,
      bio: "Expert in digital transformation and innovation management. Helped 50+ companies adopt sustainable business practices.",
      topics: ["Digital Transformation", "Innovation Strategy", "Business Sustainability"],
      social: {
        twitter: "#",
        linkedin: "#",
        website: "#"
      }
    },
    {
      id: 4,
      name: "James Kumar",
      title: "Robotics Lead",
      company: "AutoTech Solutions",
      image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=400&fit=crop&crop=face",
      category: "robotics",
      featured: false,
      bio: "Developed award-winning robotic systems for sustainable manufacturing. Passionate about human-robot collaboration.",
      topics: ["Industrial Robotics", "Automation", "Smart Manufacturing"],
      social: {
        twitter: "#",
        linkedin: "#",
        website: "#"
      }
    },
    {
      id: 5,
      name: "Dr. Lisa Wang",
      title: "Biotechnology Expert",
      company: "BioInnovate Labs",
      image: "https://images.unsplash.com/photo-1487412720507-e7ab37603c6f?w=400&h=400&fit=crop&crop=face",
      category: "biotech",
      featured: true,
      bio: "Leading researcher in sustainable biotechnology applications. Focused on bio-based materials and environmental remediation.",
      topics: ["Bio-materials", "Environmental Biotech", "Sustainable Agriculture"],
      social: {
        twitter: "#",
        linkedin: "#",
        website: "#"
      }
    },
    {
      id: 6,
      name: "Alex Thompson",
      title: "Smart Cities Director",
      company: "UrbanTech Global",
      image: "https://images.unsplash.com/photo-1519244703995-f4e0f30006d5?w=400&h=400&fit=crop&crop=face",
      category: "urban",
      featured: false,
      bio: "Transforming urban landscapes through technology. Implemented smart city solutions across 3 continents.",
      topics: ["Smart Cities", "Urban Planning", "IoT Solutions"],
      social: {
        twitter: "#",
        linkedin: "#",
        website: "#"
      }
    }
  ];

  const categories = [
    { id: 'all', name: 'All Speakers', count: speakers.length },
    { id: 'ai', name: 'AI & ML', count: speakers.filter(s => s.category === 'ai').length },
    { id: 'sustainability', name: 'Sustainability', count: speakers.filter(s => s.category === 'sustainability').length },
    { id: 'innovation', name: 'Innovation', count: speakers.filter(s => s.category === 'innovation').length },
    { id: 'robotics', name: 'Robotics', count: speakers.filter(s => s.category === 'robotics').length },
  ];

  const filteredSpeakers = filter === 'all' 
    ? speakers 
    : speakers.filter(speaker => speaker.category === filter);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50, scale: 0.9 },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <section id="speakers" ref={ref} className="py-20 relative overflow-hidden">
      {/* Background Elements */}
      <div className="absolute top-1/4 right-0 w-96 h-96 bg-accent-purple/10 rounded-full blur-3xl translate-x-1/2 -translate-y-1/2"></div>
      <div className="absolute bottom-1/4 left-0 w-80 h-80 bg-accent-cyan/10 rounded-full blur-3xl -translate-x-1/2 translate-y-1/2"></div>

      <div className="container mx-auto px-6 relative z-10">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
        >
          {/* Section Header */}
          <motion.div className="text-center mb-16" variants={itemVariants}>
            <motion.div
              className="inline-flex items-center space-x-2 text-accent-amber mb-4"
              whileHover={{ scale: 1.05 }}
            >
              <Star className="w-5 h-5" />
              <span className="font-semibold">Meet The Experts</span>
            </motion.div>
            <h2 className="text-5xl md:text-6xl font-display font-bold mb-6">
              Featured <span className="text-gradient">Speakers</span>
            </h2>
            <p className="text-xl text-gray-300 max-w-3xl mx-auto leading-relaxed">
              Learn from industry pioneers and thought leaders who are shaping the future of 
              sustainable innovation and engineering.
            </p>
          </motion.div>

          {/* Filter Buttons */}
          <motion.div className="flex flex-wrap justify-center gap-4 mb-12" variants={itemVariants}>
            {categories.map((category) => (
              <motion.button
                key={category.id}
                onClick={() => setFilter(category.id)}
                className={`px-6 py-3 rounded-2xl font-medium transition-all duration-300 ${
                  filter === category.id
                    ? 'bg-gradient-to-r from-primary-500 to-accent-purple text-white shadow-lg'
                    : 'glass-morphism text-gray-300 hover:text-white hover:border-primary-500/50'
                } border border-transparent`}
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
              >
                {category.name} <span className="text-xs opacity-70">({category.count})</span>
              </motion.button>
            ))}
          </motion.div>

          {/* Speakers Grid */}
          <motion.div 
            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
            layout
          >
            <AnimatePresence>
              {filteredSpeakers.map((speaker) => (
                <motion.div
                  key={speaker.id}
                  layout
                  initial="hidden"
                  animate="visible"
                  exit="hidden"
                  variants={itemVariants}
                  className="group relative bg-dark-300/30 glass-morphism rounded-3xl p-6 border border-white/10 hover:border-primary-500/50 transition-all duration-500 cursor-pointer overflow-hidden"
                  whileHover={{ y: -10, scale: 1.02 }}
                  onClick={() => setSelectedSpeaker(speaker)}
                >
                  {/* Featured Badge */}
                  {speaker.featured && (
                    <div className="absolute top-4 right-4 z-10">
                      <motion.div
                        className="flex items-center space-x-1 bg-gradient-to-r from-accent-amber to-accent-pink text-white px-3 py-1 rounded-full text-xs font-bold"
                        whileHover={{ scale: 1.1 }}
                      >
                        <Star className="w-3 h-3" fill="currentColor" />
                        <span>Featured</span>
                      </motion.div>
                    </div>
                  )}

                  {/* Speaker Image */}
                  <div className="relative mb-6">
                    <motion.div
                      className="w-32 h-32 mx-auto rounded-2xl overflow-hidden border-4 border-white/10 group-hover:border-primary-500/50 transition-colors duration-500"
                      whileHover={{ scale: 1.1 }}
                      transition={{ type: "spring", stiffness: 300 }}
                    >
                      <img
                        src={speaker.image}
                        alt={speaker.name}
                        className="w-full h-full object-cover"
                      />
                    </motion.div>
                    <div className="absolute inset-0 bg-gradient-to-t from-dark-400/50 to-transparent rounded-2xl"></div>
                  </div>

                  {/* Speaker Info */}
                  <div className="text-center">
                    <h3 className="text-2xl font-bold text-white mb-2">{speaker.name}</h3>
                    <p className="text-accent-cyan font-semibold mb-1">{speaker.title}</p>
                    <p className="text-gray-400 text-sm mb-4">{speaker.company}</p>

                    {/* Topics */}
                    <div className="flex flex-wrap justify-center gap-2 mb-4">
                      {speaker.topics.slice(0, 2).map((topic, index) => (
                        <span
                          key={index}
                          className="px-3 py-1 bg-primary-500/20 text-primary-300 rounded-full text-xs font-medium"
                        >
                          {topic}
                        </span>
                      ))}
                      {speaker.topics.length > 2 && (
                        <span className="px-3 py-1 bg-dark-300 text-gray-400 rounded-full text-xs">
                          +{speaker.topics.length - 2}
                        </span>
                      )}
                    </div>

                    {/* View Profile CTA */}
                    <motion.div
                      className="inline-flex items-center space-x-2 text-primary-400 text-sm font-semibold"
                      whileHover={{ x: 5 }}
                    >
                      <span>View Profile</span>
                      <ExternalLink className="w-4 h-4" />
                    </motion.div>
                  </div>

                  {/* Hover Gradient */}
                  <div className="absolute inset-0 bg-gradient-to-br from-primary-500/5 to-accent-purple/5 opacity-0 group-hover:opacity-100 transition-opacity duration-500 rounded-3xl"></div>
                </motion.div>
              ))}
            </AnimatePresence>
          </motion.div>

          {/* More Speakers CTA */}
          <motion.div className="text-center mt-16" variants={itemVariants}>
            <motion.button
              className="glass-morphism border-2 border-primary-500/30 text-primary-400 px-12 py-4 rounded-2xl font-bold text-lg hover:neon-glow transition-all duration-500"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
            >
              View All Speakers
            </motion.button>
          </motion.div>
        </motion.div>
      </div>

      {/* Speaker Modal */}
      <AnimatePresence>
        {selectedSpeaker && (
          <motion.div
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-6"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedSpeaker(null)}
          >
            <motion.div
              className="glass-morphism-intense rounded-3xl max-w-4xl w-full max-h-[90vh] overflow-y-auto"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="p-8">
                {/* Header */}
                <div className="flex justify-between items-start mb-8">
                  <div className="flex items-center space-x-4">
                    <motion.img
                      src={selectedSpeaker.image}
                      alt={selectedSpeaker.name}
                      className="w-20 h-20 rounded-2xl border-4 border-primary-500/30"
                      whileHover={{ scale: 1.1 }}
                    />
                    <div>
                      <h3 className="text-3xl font-bold text-white">{selectedSpeaker.name}</h3>
                      <p className="text-accent-cyan text-lg font-semibold">{selectedSpeaker.title}</p>
                      <p className="text-gray-400">{selectedSpeaker.company}</p>
                    </div>
                  </div>
                  <motion.button
                    onClick={() => setSelectedSpeaker(null)}
                    className="p-2 rounded-xl glass-morphism hover:bg-red-500/20 transition-colors"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <X className="w-6 h-6" />
                  </motion.button>
                </div>

                {/* Bio */}
                <div className="mb-8">
                  <h4 className="text-xl font-bold text-white mb-4">About</h4>
                  <p className="text-gray-300 leading-relaxed">{selectedSpeaker.bio}</p>
                </div>

                {/* Topics */}
                <div className="mb-8">
                  <h4 className="text-xl font-bold text-white mb-4">Expertise & Topics</h4>
                  <div className="flex flex-wrap gap-3">
                    {selectedSpeaker.topics.map((topic, index) => (
                      <motion.span
                        key={index}
                        className="px-4 py-2 bg-primary-500/20 text-primary-300 rounded-xl font-medium"
                        whileHover={{ scale: 1.05 }}
                      >
                        {topic}
                      </motion.span>
                    ))}
                  </div>
                </div>

                {/* Social Links */}
                <div className="flex items-center space-x-4">
                  <motion.a
                    href={selectedSpeaker.social.linkedin}
                    className="p-3 bg-dark-300 rounded-xl hover:bg-primary-500/20 transition-colors"
                    whileHover={{ scale: 1.1, y: -2 }}
                  >
                    <Globe className="w-5 h-5" />
                  </motion.a>
                  <motion.a
                    href={`mailto:${selectedSpeaker.social.email}`}
                    className="p-3 bg-dark-300 rounded-xl hover:bg-primary-500/20 transition-colors"
                    whileHover={{ scale: 1.1, y: -2 }}
                  >
                    <Mail className="w-5 h-5" />
                  </motion.a>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </section>
  );
};

export default Speakers;
